function solveQuadratic(a, b, c) {
    if (a === 0) {
      return "Коэффициент 'a' не может быть равен нулю в квадратном уравнении.";
    }
  
    const discriminant = b * b - 4 * a * c;
  
    if (discriminant > 0) {
      const x1 = (-b + Math.sqrt(discriminant)) / (2 * a);
      const x2 = (-b - Math.sqrt(discriminant)) / (2 * a);
      return `Уравнение имеет два различных действительных корня: x1 = ${x1}, x2 = ${x2}`;
    } else if (discriminant === 0) {
      const x = -b / (2 * a);
      return `Уравнение имеет один действительный корень: x = ${x}`;
    } else {
      return "Уравнение не имеет действительных корней.";
    }
  }

module.exports = { solveQuadratic };